jQuery(function($) {
    $('.instagram').on('didLoadInstagram', function(event, response) {
        var img = document.getElementById('instagram-picture');
        var msg = document.getElementById('instagram-text');
        img.src = response.data[0].images.low_resolution.url;
        msg.innerText = response.data[0].caption.text;
        $('#instatext').innerHTML=response.data[0].caption.text;
    });
    $('.instagram').instagram({
        hash: 'landskapskompaniet',
        accessToken: '1459532749.ab103e5.4e9c9b38a54145338fc7263289159c95'
    });
});